SELECT		hist.issue_id,  	i.key,  	hist.value AS status_id,  	s.name AS status_name,  	hist.timeFROM jra.issue_field_history AS histINNER JOIN jra.issue AS i	ON hist.issue_id = i.idINNER JOIN(	SELECT field.issue_id	FROM jra.vw_latest_issue_field_value AS field	WHERE		1 = 1			AND field.field_id = 'status'		AND field.time::DATE >= '2023-12-14'		AND field.author_id = '63c50741cd6a09abe71e007c' -- Bryan ) AS latest  	ON hist.issue_id = latest.issue_id  LEFT JOIN jra.status AS s  	ON hist.value = s.id  WHERE  	1 = 1  	AND hist.field_id = 'status'